import React, { useState } from "react";
import styled from "styled-components";

const Div = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
`;

const Index = () => {
  const [theme, setTheme] = useState("blue");

  console.log(theme);

  return (
    <Div>
      <div className="input-btns">
        <input
          type="radio"
          name="theme"
          id="red"
          value="red"
          onChange={(e) => {
            setTheme(e.target.value);
          }}
          checked={theme === "red"}
        />
        red
        <input
          type="radio"
          name="theme"
          id="black"
          value="black"
          onChange={(e) => {
            setTheme(e.target.value);
          }}
          checked={theme === "black"}
        />
        black
        <input
          type="radio"
          name="theme"
          id="blue"
          value="blue"
          onChange={(e) => {
            setTheme(e.target.value);
          }}
          checked={theme === "blue"}
        />
        blue
      </div>
    </Div>
  );
};

export default Index;
