import React, { useState } from "react";
import styled from "styled-components";

const Div = styled.div``;

const Index = () => {
  const [scheme, setScheme] = useState("blue");

  console.log(scheme);

  return (
    <div className="input-btns">
      <input
        type="radio"
        name="scheme"
        id="red"
        value="red"
        onChange={(e) => {
          setScheme(e.target.value);
        }}
        checked={scheme === "red"}
      />
      red
      <input
        type="radio"
        name="scheme"
        id="black"
        value="black"
        onChange={(e) => {
          setScheme(e.target.value);
        }}
        checked={scheme === "black"}
      />
      black
      <input
        type="radio"
        name="scheme"
        id="blue"
        value="blue"
        onChange={(e) => {
          setScheme(e.target.value);
        }}
        checked={scheme === "blue"}
      />
      blue
    </div>
  );
};

export default Index;
